mapper.select:
SELECT product.pid, classification.name AS classification_name, enterprise.name AS enterprise_name, product.photo, product.code, product.name, product.unit, product.is_major, product_record.rid, account_info.name AS info_name, product_record.num, product_record.approver, product_record.statue, product_record.process_time, product_record.insert_time, product_record.import_type FROM product JOIN product_record ON product.pid = product_record.pid JOIN classification ON product.cid = classification.cid JOIN enterprise ON product.eid = enterprise.eid JOIN account_info ON product_record.aid = account_info.aid
1 = 1
AND product.name LIKE CONCAT('%', #{condition.name}, '%') OR product.code LIKE CONCAT('%', #{condition.name}, '%')
AND enterprise.social_code LIKE CONCAT('%', #{condition.enterprise}, '%') OR enterprise.name LIKE CONCAT('%', #{condition.enterprise}, '%')
AND classification.name = #{condition.classification}
AND product.is_major = #{condition.isMajor}
AND product_record.import_type = #{condition.importType}
AND product_record.statue = #{condition.statue}
ORDER BY product_record.insert_time DESC
